root.codeblock:
// ta pętla wykona kod zawarty w {} 3 razy.

// struktura pętli for: for(*zmienna*;*warunek*;*inkrementacja lub dekrementacja*)

for (let i = 0; i  <= 3; i++) 
{
  console.log(i)
}

// to same ale wykonane przy użyciu pętli while

// struktura pętli while: while (*warunke*)

var x = 0;

while (x <= 3)
{

  console.log(x)
  x = x+1;

}
